mapper.select:
select 

from t_trade_service_order
where  order_no = #{orderNo,jdbcType=VARCHAR} and order_status =  #{orderStatus,typeHandler=com.zizaike.trade.dao.handler.OrderStatusHandler,javaType=com.zizaike.entity.trade.OrderStatus}

select 

from t_trade_service_order
where  customer_id = #{customerId,jdbcType=INTEGER} 

   and  order_status =  #{orderStatus,typeHandler=com.zizaike.trade.dao.handler.OrderStatusHandler,javaType=com.zizaike.entity.trade.OrderStatus}


   and  CONCAT (order_no,unit_name) LIKE CONCAT('%','${keywords,jdbcType=VARCHAR}','%' )

select 

from t_trade_service_order
where  
	business_id = #{businessId,jdbcType=INTEGER} 

   and  business_order_status =  #{businessOrderStatus,typeHandler=com.zizaike.trade.dao.handler.BusinessOrderStatusHandler,javaType=com.zizaike.entity.trade.BusinessOrderStatus}


   and  order_status =  #{orderStatus,typeHandler=com.zizaike.trade.dao.handler.OrderStatusHandler,javaType=com.zizaike.entity.trade.OrderStatus}


   and  (create_at between   #{orderTimeBegin, jdbcType=TIMESTAMP} and #{orderTimeEnd, jdbcType=TIMESTAMP})


   and  (use_time between  #{useTimeBegin, jdbcType=TIMESTAMP} and #{useTimeEnd, jdbcType=TIMESTAMP})


   and  order_no =  #{orderNo,jdbcType=VARCHAR}


   and  guest_name =  #{guestName,jdbcType=VARCHAR}


   and  mobile =  #{mobile,jdbcType=VARCHAR}

order by  create_at  desc
